import CIPPacketHandler from '../CIPPacketHandler';
/**
 * Handles the reception of the PROGRAM_READY packet
 * @param packet <0x0F> <0x00> <0x01> <Status>
 *
 * Status = 0x00
 * Control System is loading firmware;
 * Client should disconnect and retry in 15 seconds
 *
 * Status = 0x01
 * Simpl Windows Program is not running;
 * Client should stay connected and wait for a "Program Ready" (2) status
 *
 * Status = 0x02
 * Program Ready;
 * Client can now send the CONNECT message
 *
 */
export default function handleProgramReady(this: CIPPacketHandler, packet: Uint8Array): void;
