import CIPEventTypes from "../cip/CIPEventTypes";
/**
 *
 * This function is supposed to construct an Uint8Array[<packetType>, <length>, <length>, <handler?>, <handler?>, <data> .... <data>]
 * Each <> represents a byte
 * This function basically concatenates
 *        the type of event ( 1 byte )
 *        the length of upcoming data (that is, without the type of event but also includes the handlers if they are present ) ( 2 bytes )
 *        the handlers (if required) ( 2 bytes )
 *        the data (unknown bytes)
 *
 * @param packetType -- 1 byte, type CIPEventTypes
 * @param cresnetDataPacket -- An array, unknown length
 * @param virtualConnectionHandle -- 2 bytes, just for some CIPEventTypes
 */
export default function buildCIPPacket(packetType: CIPEventTypes, cresnetDataPacket: Uint8Array | null, virtualConnectionHandle?: number): Uint8Array;
